import React, {Component} from "react"
import './App.css';
import Card from "./components/Card/Card";
import Jogar from "./components/Jogar/Jogar";
import bd from './bancoDeDados/bancoDeDados.json';
import Baralho from "./components/Baralho/Baralho"
import Mandante from "./components/Jogador/Mandante"
import Visitante from "./components/Jogador/Visitante"

class App extends Component{
  constructor(){
    super();

    this.state = {
      cartas:[],
      mandante:[],
      visitante:[]

    }
  }

   PegarDoBanco(){
    const dados = []
    for(let i=0;i<bd.length;i++){
      dados.push({
        id:bd[i].id,
        país:bd[i].país,
        idh:bd[i].idh,
        exp:bd[i].exp
      })
    } 
    return dados
   }

  DistribuirCartas(indices){
    console.log(indices)
    var arrays = []
    if(indices.lenght%2==0){
      arrays = dividirArray(indices)
    }
    else{
      indices.pop()
      arrays = dividirArray(indices)
    }
    function dividirArray(array){
      let tamanho = array.lenght;
      let a1 = array.slice(0,tamanho/2)
      let a2 = array.slice(tamanho/2)      
      return {a1:a1,a2:a2}
    }

    console.log('arrays divididos',arrays)
/*     setTimeout(()=>{
      this.setState({
        mandante:[indices[0],indices[1]],
        visitante:[indices[2],indices[3]]
      })
      console.log('new state is',this.state)
    },2000) */

  }


  render(){
    return(
      <section className="conteudo">

        <div id='mandante'>
          <h2>Mandante</h2>
        </div>
        <div id='visitante'>
        <h2>Visitante</h2>
        </div>
        <Baralho cartas={this.PegarDoBanco()}/>
         <Jogar class="classeJogar"  DistribuirCartas={this.DistribuirCartas.bind(this)}/>
        <Mandante className='mandante' mao={this.state.mandante}/>
        <Visitante className='visitante' mao={this.state.visitante}/>

      </section>
    )
  }


}

