import React, {Component} from "react";
import Card from "../Card/Card";
import "./estilos.css";


class Baralho extends Component{

    constructor(props){
        super(props);
        
    }

    _moverParaMandante(indiceDaCarta){
        console.log('estou no baralho e carta é',indiceDaCarta)
        this.props.moverPraMandante(indiceDaCarta)

    }
         
    render(){
        
        return (
            
         <ul>
         
             {this.props.cartas.map((cartas,index)=>{
                 return(
                     <li >
                     <button onClick={this._moverParaMandante.bind(this)}>oi</button>
                         <Card 
                           onClick={this._moverParaMandante.bind(this)}
                           id={cartas.id}
                           país={cartas.país} idh={cartas.idh} exp={cartas.exp}/>
                     </li>
                 )
             })}
             
         </ul>
            
        
       
        ); 
    }
}

export default Baralho;

