import React, {Component} from "react";
import Card from "../Card/Card";
import Baralho from "../Baralho/Baralho"

class Mesa extends Component{
    constructor(props){
        super(props)
    }

    render(){
        return(
           <ul>
               {this.props.cartas.map((c,index)=>{
                   return(
                       <li key={index}>
                          <CardVerso classe={this.props.classe}/>
                       </li>
                   )
               })}
           </ul> 
        )
    }

}

export default Mesa;
