import React, { Component } from "react";
import "./estilos.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import bd from '../../bancoDeDados/bancoDeDados.json';

class Jogar extends Component {
    constructor(){
        super();
        this.numeroDaCarta ="";
    }
    
    handleIniciarJogo(){
        let totalDeCartas = bd.length;
        let n = []
        for(let i =0;i<totalDeCartas;i++){
            n.push(i)
        }
        let arrayRandom = n.shuffle()
        console.log('array random',arrayRandom)
        this.numeroDaCarta = Math.floor(Math.random() * 4);
        console.log('carta aleatória é: ',this.numeroDaCarta)
    }

    render() {
        return (
            <section>
                <button 
                onClick={this.handleIniciarJogo.bind(this)}
                >Jogar</button>
                <div>
                    <label>Placar</label>
                    <div>10</div>
                </div>
            </section>
        )
    }
}

export default Jogar
