import React, {Component} from "react";
import Card from "../Card/Card";
import "./estilos.css";
import bd from "../../bancoDeDados/bancoDeDados.json"

class Baralho extends Component{
 constructor(){
     super();
     this.state = {
         cartas:[]
     }
 }  

    GerarCartas(){
        const cartas = [];
        for(let i=0;i<bd.length;i++){
            cartas.push({
                pais:bd[i].país,
                idh:bd[i].idh,
                exp:bd[i].exp
            })
        }
        this.setState(cartas)
    }
  
    render(){
        return (
         <ul>
             {this.state.cartas.map((cartas,index)=>{
                 return(
                     <li key={index}>
                         <Card pais={cartas.pais} idh={cartas.idh} exp={cartas.exp}/>
                     </li>
                 )
             })}
             
         </ul>
            
        
       
          ); 
    }
}

export default Baralho;

