import React, {Component} from "react";
import Card from "../Card/Card";
import "./estilos.css";
import bd from "../../bancoDeDados/bancoDeDados.json"

class Baralho extends Component{
 constructor(props){
     super(props);
     this.cartas = "",
     this.state = {
         cartas:[]
     }
 }  

    GerarCartas(){
        const cartas = [];
        for(let i=0;i<bd.length;i++){
            cartas.push({
                pais:bd[i].país,
                idh:bd[i].idh,
                exp:bd[i].exp
            })
        }
        console.log('gerar cartas',cartas)
       /*  this.setState(cartas) */
       this.cartas = cartas
    }
  
    render(){
        console.log('this.cartas',this.state.cartas)
        return (
            
         <ul onLoad={() => this.GerarCartas()}>
             {this.props.cartas.map((cartas,index)=>{
                 return(
                     <li key={index}>
                         <Card pais={cartas.pais} idh={cartas.idh} exp={cartas.exp}/>
                     </li>
                 )
             })}
             
         </ul>
            
        
       
          ); 
    }
}

export default Baralho;

