import React, { Component } from "react";
import Card from "../Card/Card";
import Baralho from "../Baralho/Baralho";

class Mandante extends Component {
    constructor(props){
        super(props)
    }


    
    _JogadaMandante(elemento){
        console.log('elemento',elemento)
        var nome = elemento.getAttribute('name')
        if(nome=='idh'){
            var jogada = {
                categoria:'idh',
                valor:elemento.textContent
            }
            this.props.JogadaMandante(jogada)
        }
        else if(nome=='exp'){
            var jogada = {
                categoria:'exp',
                valor:elemento.textContent
            }
            this.props.JogadaMandante(jogada)
        }
       
    }

    render(){
        
        return (
            
         <ul>
         
             {this.props.cartas.map((cartas,index)=>{
                 return(
                     <li  className="mandante" key={'mandante'+index} onClick={(x)=>this._JogadaMandante(x.target)}>
                    
                         <Card
                             
                          bandeira={cartas.bandeira}
                           id={cartas.id}
                           país={cartas.país} idh={cartas.idh} exp={cartas.exp}/>
                     </li>
                 )
             })}
             
         </ul>
            
        
       
        ); 
    }

}

export default Mandante;
