import React, {Component} from "react"
import './App.css';
import Card from "./components/Card/Card";
import Jogar from "./components/Jogar/Jogar";
import bd from './bancoDeDados/bancoDeDados.json';
import Baralho from "./components/Baralho/Baralho"

class App extends Component{
  constructor(){
    super();
    this.cartas = "",
    this.state = {
      cartas:[]
    }
  }

  criarBaralho(){
    var cartas = []
    for(let i=0;i<bd.length;i++){
      cartas.push({
        pais:bd[i].país,
        idh:bd[i].idh,
        exp:bd[i].exp
      })
    }
    this.cartas = cartas;
  }


