    const novoEstado = {
      cartas:cartas
    }
    this.setState(novoEstado)
  } */

  render(){
    return(
      <section className="conteudo">
        <div id='mandante'>
          <h2>Mandante</h2>
        </div>
        <div id='visitante'>
        <h2>Visitante</h2>
        </div>
        <Baralho cartas={this.cartas}/>
        <Jogar class="classeJogar" criarCarta={this.criarCarta.bind(this)}/>
      </section>
    )
  }


}

