import React, { Component } from "react";
import "./estilos.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import bd from '../../bancoDeDados/bancoDeDados.json';



class Card extends Component {
   static n = 0;
    constructor(props){
       super(props);
        
     
   }
   
   
   
   contador(){
    Card.n += 1;
    this.x = Card.n
    return this.x
    }

    _Jogou(evento){
        console.log('evento é:',evento)
        console.log('pai',evento.parentNode.parentNode.parentNode.parentNode.getAttribute('class'))
       
        let ehMandante = evento.parentNode.parentNode.parentNode.parentNode.getAttribute('class')

        

        if(ehMandante!='mandante'){
            return
        }
        else{
            let dadosClicados = {
                campo:evento.name,
                valor:evento.textContent
                
            }
    
           console.log('dados clicados',dadosClicados)
    
         console.log('props são',this.props)
         this.props.MandanteJogou(dadosClicados)
        }

 

    }

  

    render() {
        
        return (
           <section className='card' name='xy' id={this.props.id}>
           <img className="card-img-top" src={this.props.bandeira} alt={this.props.país}></img>
            <div className='card-body'>
            <h5>{this.props.país}</h5>
            </div>
                <ul className="list-group list-group-flush" >
                   
                   <label>IDH</label>
                    <li className="list-group-item">
                    <button name="idh" className="btn btn-primary btn-lg btn-block" onClick={(x)=> this._Jogou(x.target)}>{this.props.idh}</button>
                    </li>
                    <label>Expectativa de vida</label>
                    <li className="list-group-item">
                    <button name='exp' className="btn btn-primary btn-lg btn-block">{this.props.exp}</button>
                    </li>
                </ul>
            </section>
        )
    }
}

export default Card

