import React, {Component} from "react"
import './App.css';
import Card from "./components/Card/Card";
import Jogar from "./components/Jogar/Jogar";
import bd from './bancoDeDados/bancoDeDados.json';
import Baralho from "./components/Baralho/Baralho"

class App extends Component{
  constructor(){
    super();

    this.state = {
      cartas:[]
    }
  }

   CriarBaralho(evento){
    console.log('entrei no criar baralho')
    evento.preventDefault();
    evento.stopPropagation();
    const cartas = []
    for(let i=0;i<bd.length;i++){
      cartas.push({
        pais:bd[i].país,
        idh:bd[i].idh,
        exp:bd[i].exp
      })
    }
    this.setState(cartas)
  } 



  render(){
    return(
      <section className="conteudo" >
      <button onClick={this.CriarBaralho.bind(this)} >Oi</button>
        <div id='mandante'>
          <h2>Mandante</h2>
        </div>
        <div id='visitante'>
        <h2>Visitante</h2>
        </div>
        <Baralho cartas={this.state.cartas} />
        <Jogar class="classeJogar"/>

      </section>
    )
  }


}

