import React, { Component } from "react";
import "./estilos.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import bd from '../../bancoDeDados/bancoDeDados.json';
import info from "./info.svg"



class Card extends Component {
   static n = 0;
    constructor(props){
       super(props);
        
     
   }
   
   
   
   contador(){
    Card.n += 1;
    this.x = Card.n
    return this.x
    }


    _cliqueInfo(evento){
        console.log('foi clicado no info')
        console.log('tag', evento)
       
    }
    
    render() {
       
      
        var ultimaJogada = this.props.ultimaJogada
        if(this.props.resultado){
            var r = this.props.resultado
            var classeBotao;
            if(r==null){
               
                 classeBotao = 'btn btn-secondary btn-block'
            }
            else if(r=='Ganhou!'){
                 classeBotao = 'btn btn-success btn-block'
            }
            else if(r=='Empatou!'){
                 classeBotao = 'btn btn-warning btn-block'
            }
            else{
                 classeBotao = 'btn btn-danger btn-block'
            }
        }
      
        
        return (
          
            <section className={this.props.transicao ? 'card bg-light mb-3 teste' : 'card bg-light mb-3'} name='xy' id={this.props.id}>
           <img className="card-img-top" src={this.props.bandeira} alt={this.props.pais}></img>
            <h5 className='nomePais'>{this.props.pais}</h5>
            <div className='card-body'>
            
                <ul className="list-group list-group-flush" >
                   
                    <li className="list-group-item">
                   <label>IDH</label><img src={info} className='info' onClick={(x)=> this._cliqueInfo(x.target)} name='idh'></img>
                    <button name="idh" className = {ultimaJogada=='idh'? classeBotao : 'btn btn-secondary btn-block'}>{this.props.idh}</button>
                    </li>
                    <li className="list-group-item">
                    <label>Expectativa de vida</label><img src={info} className='info' onClick={(x)=> this._cliqueInfo(x.target)} name='exp'></img>
                    <button name='exp' className = {ultimaJogada=='exp'? classeBotao : 'btn btn-secondary btn-block'}>{this.props.exp}</button>
                    </li>
                </ul>
                </div>
            </section>
           
           
        )
    }
}

export default Card

