import React, {Component} from "react"
import './App.css';
import Card from "./components/Card/Card";
import Jogar from "./components/Jogar/Jogar";
import bd from './bancoDeDados/bancoDeDados.json';
import Baralho from "./components/Baralho/Baralho"
import Mandante from "./components/Jogador/Mandante"
import Visitante from "./components/Jogador/Visitante"

class App extends Component{
  constructor(){
    super();

    this.state = {
      mesa:[],
      mandante:[],
      visitante:[]

    }
  }

   TodosIds(){

    let todosIds = []
     for(let i=0;i<bd.length;i++){
       todosIds.push(bd[i].id)
     }

     console.log('estou no TodosIds',todosIds)
     this.setState({
       mesa:todosIds})
     //return todosIds
   }

   PegarDoBanco(ids){
    console.log('estou no pegar do banco',ids)
    const dados = []
    ids.map((ids)=>{
      for(let i=0;i<bd.length;i++){
        if(ids==bd[i].id){
          dados.push({
            id:bd[i].id,
            país:bd[i].país,
            idh:bd[i].idh,
            exp:bd[i].exp
          })
        }
      }
    })

    console.log('pegar do banco',dados)
    return dados
   }

  DistribuirCartas(indices){
    console.log(indices)
    var arrays;
    if(indices.length%2==0){
      console.log('estou dividino certo')
      arrays = dividirArray(indices)
    }
    else{
      indices.pop()
      arrays = dividirArray(indices)
    }
    function dividirArray(array){
      let tamanho = array.length;
      let a1 = array.slice(0,tamanho/2)
      let a2 = array.slice(tamanho/2)      
      return {a1:a1,a2:a2}
    }



    console.log('arrays divididos',arrays)
    this.setState({
      mesa:[],
      mandante: this.PegarDoBanco(arrays.a1),
      visitante:this.PegarDoBanco(arrays.a2)

    })



  }





  render(){
    return(
      <section className="conteudo">

        <div id='mandante'>
          <h2>Mandante</h2>
        </div>
        <div id='visitante'>
        <h2>Visitante</h2>
        </div>
         {/* <Baralho  cartas={this.PegarDoBanco(this.TodosIds())}/> */}
         <Baralho  a={this.TodosIds()} cartas={this.state.mesa}/>
         <Jogar class="classeJogar"  DistribuirCartas={this.DistribuirCartas.bind(this)}/>
        <Mandante className='mandante' cartas={this.state.mandante}/>
        <Visitante className='visitante' cartas={this.state.visitante}/>

      </section>
    )
  }


}

