import React, { Component } from "react";
import Card from "../Card/Card";
import Baralho from "../Baralho/Baralho";
import "./estilos.css"

class Mandante extends Component {
    constructor(props) {
        super(props)
    }



    _JogadaMandante(elemento) {

        var nome = elemento.getAttribute('name')
        if (nome == 'idh'|nome=='exp') {

            this.props.JogadaMandante(nome)
        }
     
    }

    
    render() {
        console.log('props no mandante',this.props)
        if(this.props.carta==null){
            return(
               null
            )
        }else{

            return (
                <section  key={this.props.id} onClick={(x) => this._JogadaMandante(x.target)} className={this.props.visivel ? 'block' : 'none'} >
    
                    <Card
                        efeitoCartas={this.props.efeitoCartas}
                        resultado={this.props.resultado}
                        ultimaJogada={this.props.ultimaJogada}
                        bandeira={this.props.carta.bandeira}
                        id={this.props.carta.id}
                        país={this.props.carta.país} idh={this.props.carta.idh} exp={this.props.carta.exp} />
                </section>
            )
        }
    }
     


}

export default Mandante;



