import React, {Component} from "react"
import './App.css';
import Card from "./components/Card/Card";
import Jogar from "./components/Jogar/Jogar";
import bd from './bancoDeDados/bancoDeDados.json';
import Baralho from "./components/Baralho/Baralho"
import Mandante from "./components/Jogador/Mandante"
import Visitante from "./components/Jogador/Visitante"
import Cabecalho from "./components/cabecalho/Cabecalho";
import Bandeiras from "./components/Bandeiras/Bandeiras"
import { findFlagUrlByCountryName } from "country-flags-svg";

//ideia -> Pegar os primeiros elementos dos arrays para jogar

class App extends Component{
  constructor(){
    super();

    this.state = {
      mesa:true,
      mandante:[],
      visitante:[]

    }
  }

   TodosIds(){

    let todosIds = []
     for(let i=0;i<bd.length;i++){
       todosIds.push(bd[i].id)
     }


     return todosIds
   }

   PegarDoBanco(ids){

    const dados = []
    ids.map((ids)=>{
      for(let i=0;i<bd.length;i++){
        if(ids==bd[i].id){
          dados.push({
            id:bd[i].id,
            país:bd[i].país,
            idh:bd[i].idh,
            exp:bd[i].exp,
            bandeira:findFlagUrlByCountryName(bd[i].país)

          })
        }
      }
    })




    return dados
   }

  DistribuirCartas(indices){

    var arrays;
    if(indices.length%2==0){

      arrays = dividirArray(indices)
    }
    else{
      indices.pop()
      arrays = dividirArray(indices)
    }
    function dividirArray(array){
      let tamanho = array.length;
      let a1 = array.slice(0,tamanho/2)
      let a2 = array.slice(tamanho/2)      
      return {a1:a1,a2:a2}
    }



    this.setState({
      mesa:false,
      mandante: this.PegarDoBanco(arrays.a1),
      visitante:this.PegarDoBanco(arrays.a2)

    })



  }

  BandeirasInicio(){
    let ordenaPaises = []
    for(let i=0;i<bd.length;i++){
      ordenaPaises.push(bd[i].país)
    }
    ordenaPaises.sort()

    let arrayBandeiras = [];

    for(let i=0;i<ordenaPaises.length;i++){
      arrayBandeiras.push({
        nome:ordenaPaises[i],
        bandeira:findFlagUrlByCountryName(ordenaPaises[i])
      })
    }

    return arrayBandeiras

  }

 JogadaMandante(){
   console.log('estou no jogada mandante')
 }

  render(){
    return(
      <section className="conteudo">
        <div id='cabecalho'>
        <Cabecalho />
        </div>
        <div id='bandeiras'>
          <Bandeiras bandeirasInicio={this.BandeirasInicio()}/>
        </div>
        <div id="principal">

        <div id='mandante'>
          <h2>Mandante</h2>
        <Mandante  cartas={this.state.mandante} JogadaMandante={this.JogadaMandante.bind(this)}/>
        </div>
        <div id='visitante'>
        <h2>Visitante</h2>
        <Visitante cartas={this.state.visitante} />
        </div>
        <div id='jogar'>

         <Jogar   DistribuirCartas={this.DistribuirCartas.bind(this)}/>
         <Baralho  cartas={this.PegarDoBanco(this.TodosIds())} visivel={this.state.mesa}  />
        </div>
        </div>


      </section>
    )
  }


}

{/* <Baralho  cartas={this.PegarDoBanco(this.TodosIds())} /> */}

export default App;
