import React, {Component} from "react";
import CardVerso from "../CardVerso/CardVerso";
import "./estilos.css"

class Mesa extends Component{
    constructor(props){
        super(props)
    }

    render(){
       console.log('props mesa',this.props)
       if(!this.props.jogoEmAndamento){
           return(null)
       }
       else{

           return(
              <ul className='listaCards'>
                  {this.props.cartas.map((c,index)=>{
                      return(
                          <li key={index}>
                             <CardVerso classe='pequeno'/>
                          </li>
                      )
                  })}
              </ul> 
           )
       }
    }

}

export default Mesa;
