import React, { Component } from "react";
import "./estilos.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import bd from '../../bancoDeDados/bancoDeDados.json';

class Jogar extends Component {
    constructor(props){
        super(props);
        this.numeroDaCarta ="";
    }
    
    handleIniciarJogo(){
        let totalDeCartas = bd.length;
        let n = []
        for(let i =0;i<totalDeCartas;i++){
            n.push(i)
        }
       
        n.sort(function(a,b){ return (Math.round(Math.random())-0.5); });
       
        console.log('aR',n)
        this.bind.DistribuirCartas(n)
                
    }



    render() {
        return (
            <section>
                <button 
                onClick={this.handleIniciarJogo.bind(this)}
                className="btn btn-primary botao_iniciar">Iniciar Jogo</button>
                <div>
                    <label>Placar</label>
                    <div>10</div>
                </div>
            </section>
        )
    }
}

export default Jogar
