import React, {Component} from "react"
import './App.css';
import Card from "./components/Card/Card";
import Jogar from "./components/Jogar/Jogar";
import bd from './bancoDeDados/bancoDeDados.json';
import Baralho from "./components/Baralho/Baralho"
import Mandante from "./components/Jogador/Mandante"
import Visitante from "./components/Jogador/Visitante"
import Cabecalho from "./components/cabecalho/Cabecalho";
import Bandeiras from "./components/Bandeiras/Bandeiras"
import { findFlagUrlByCountryName } from "country-flags-svg";
import Placar from "./components/Placar/Placar";

import Mesa from  "./components/Mesa/Mesa"

//ideia -> Pegar os primeiros elementos dos arrays para jogar

class App extends Component{
  constructor(){
    super();

    this.state = {
      mesa:Array.from({length: bd.length}, (v,k) => k + 1),
      mandante:{
        carta: null,
        visivel:false,
        placar:0
      },
      visitante:{
        carta: null,
        visivel:false,
        placar:0
      },
      resultado:{
        texto:'testando elemento',
        visivel:false,
      }


    }
  }

   TodosIds(){

    let todosIds = []
     for(let i=0;i<bd.length;i++){
       todosIds.push(bd[i].id)
     }


     return todosIds
   }

   PegarDoBanco(ids){

    if(Array.isArray(ids)){
      const dados = []
      ids.map((ids)=>{
        for(let i=0;i<bd.length;i++){
          if(ids==bd[i].id){
            dados.push({
              id:bd[i].id,
              país:bd[i].país,
              idh:bd[i].idh,
              exp:bd[i].exp,
              bandeira:findFlagUrlByCountryName(bd[i].país)

            })
          }
        }
      })

      return dados
    }
    else{
      return (
        {
          id:bd[ids].id,
              país:bd[ids].país,
              idh:bd[ids].idh,
              exp:bd[ids].exp,
              bandeira:findFlagUrlByCountryName(bd[ids].país)
        }
      )
    }


   }

  DistribuirCartas(indices){


    if(indices.length%2==0){
      var arrayDeCartas = indices;  

    }
    else{
      indices.pop()
      var arrayDeCartas = indices;
    }

      console.log('array de cartas é',arrayDeCartas)  

      var estadoAntigo = this.state

      console.log('a carta do mandante é:',this.PegarDoBanco(arrayDeCartas[0]))
      var remover2 = indices.splice(0,2)    
      this.setState({
      mesa:indices,
      mandante: {
        carta: this.PegarDoBanco(arrayDeCartas[0]),
        visivel:true,
        placar:estadoAntigo.mandante.placar
      },
      visitante: {
        carta:this.PegarDoBanco(arrayDeCartas[1]),
        visivel:false,
        placar:estadoAntigo.visitante.placar
      },



    })



  }

  BandeirasInicio(){
    let ordenaPaises = []
    for(let i=0;i<bd.length;i++){
      ordenaPaises.push(bd[i].país)
    }
    ordenaPaises.sort()

    let arrayBandeiras = [];

    for(let i=0;i<ordenaPaises.length;i++){
      arrayBandeiras.push({
        nome:ordenaPaises[i],
        bandeira:findFlagUrlByCountryName(ordenaPaises[i])
      })
    }

    return arrayBandeiras

  }

 JogadaMandante(jogada){
   console.log('estou no app e jogada é',jogada)

   let prevState = this.state

   this.setState({visitante:{
    carta:prevState.visitante.carta, 
    visivel:true,
    placar:prevState.visitante.placar
   }}) 

  console.log('novo estado',this.state)
  setTimeout(()=>{
    console.log('novo estado time',this.state)
  })

  var buscarValor = this.state.mandante.carta[jogada]
   console.log('o valor é',buscarValor)
   var vencedor = this.CompararJogada(jogada,this.state.mandante.carta[jogada],this.state.visitante.carta[jogada])
    this.AlterarPlacar(vencedor) 

 }

 AlterarPlacar(vencedor){
  console.log('this.state',this.state)
  let prevState = this.state
  if(vencedor=='mandante'){
     var a = this.state.mandante.placar;
     console.log('atual',a)
     this.setState({
       mandante:{
        carta:prevState.mandante.carta,
        visivel:prevState.mandante.visivel, 
        placar:a+1
       },
       resultado:{
         texto:'Ganhou!',
         visivel:true
       }
     })
    }
  else if(vencedor=='visitante'){
    console.log('vencedor foi o visitante')

    var a = this.state.visitante.placar;
    console.log('atual',a)
    this.setState({
      visitante:{
        carta:prevState.visitante.carta,
        visivel:prevState.visitante.visivel,
        placar:a+1
      },
      resultado:{
        texto:'Perdeu!',
        visivel:true
      }

    })
  }
 }

 CompararJogada(tipo,valorMandante,valorVisitante){
   if(tipo=='idh'|tipo=='exp'){
     if(valorMandante==valorVisitante){
       return 'empate'
     }
     else if(valorMandante>valorVisitante){
       return 'mandante'
     }
     else{
       return 'visitante'
     }
   }
 }


  render(){
    return(
      <section className="conteudo">
        <div id='cabecalho'>
        <Cabecalho />
        </div>
        <div id='bandeiras'>
          <Bandeiras bandeirasInicio={this.BandeirasInicio()}/>
        </div>
        <div id="principal">

        <div id='mandante'>
          <h2>Mandante</h2>
        <Mandante  carta={this.state.mandante.carta} JogadaMandante={this.JogadaMandante.bind(this)} visivel={this.state.mandante.visivel} />
        </div>
        <div id='visitante'>
        <h2>Visitante</h2>
        <Visitante carta={this.state.visitante.carta} visivel={this.state.visitante.visivel} />
        </div>
        <div id='jogar'>

         <Jogar   DistribuirCartas={this.DistribuirCartas.bind(this)} resultado={this.state.resultado}/>
         <Mesa cartas={this.state.mesa}/>
        </div>
        </div>
        <div id='placar'>
          <Placar placarMandante={this.state.mandante.placar} placarVisitante={this.state.visitante.placar}/>
        </div>



      </section>
    )
  }


}

{/* <Baralho  cartas={this.PegarDoBanco(this.TodosIds())} visivel={this.state.mesa}  /> */}

export default App;
