import React, {Component} from "react"
import './App.css';
import Card from "./components/Card/Card";
import Jogar from "./components/Jogar/Jogar";
import bd from './bancoDeDados/bancoDeDados.json';
import Baralho from "./components/Baralho/Baralho"

class App extends Component{
  constructor(){
    super();
    this.pais="";
    this.indice="";
    this.state = {
      cartas:[]
    }
  }

  criarCarta(numero){
    console.log('numero é',numero)
    const dados = bd[1]
    const novoEstado = {
      país:dados.país,
      indice:dados.indice
    }
    this.setState(novoEstado)
  }

  render(){
    return(
      <section className="conteudo">
        <Baralho cartas={this.state.cartas}/>
        <Jogar criarCarta={this.criarCarta.bind(this)}/>
      </section>
    )
  }


}



export default App;
