import React, {Component} from "react";
import Card from "../Card/Card";
import "./estilos.css";


class Baralho extends Component{

    constructor(props){
        super(props);
        
    }

    _mover(evento){
        console.log('evento é',evento.target)
        console.log('foi clicado no id',evento.target.parentNode.getAttribute('id'))
    }
         
    render(){
        
        return (
            
         <ul>
         
             {this.props.cartas.map((cartas,index)=>{
                 return(
                     <li id={'bar'+index} >
                    
                         <Card 
                          onClick={this._mover.bind(this)}
                           id={cartas.id}
                           país={cartas.país} idh={cartas.idh} exp={cartas.exp}/>
                     </li>
                 )
             })}
             
         </ul>
            
        
       
        ); 
    }
}

export default Baralho;

