import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import "../../assets/Jogar.css";

class NumeroDeCartas extends Component{
    constructor(props){
        super(props)
    }

   render(){
       
    if(this.props.visivel){
           return(null)
       }
       else{
        return(
            <section>
                 <label htmlFor='testeNumeroDeCartas'>Número de cartas (por jogador)</label>
                 <br></br>
                 <input type='number' id='testeNumeroDeCartas'></input>

                 <br></br>
                     <label >Escolher cartas</label>
                     <br></br>
                     <label>Aleatóriamente</label>
                     <input type='radio' name='qlCartas' value='aleatorias'></input>
                     <label>Mais altas</label>
                     <input type='radio' name='qlCartas' value='melhores'></input>
                     <label>Mais baixas</label>
                     <input type='radio' name='qlCartas' value='piores'></input>
                     
                
                                   
            </section>
 
        )
       }
       
   }
}

export default NumeroDeCartas;
