import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import "../../assets/Informacao.css";
import sair from "../../assets/imagens/sair.svg"
import inf from "./bancoInformacoes"


class Informacao extends Component {
    static n = 0;
    constructor(props) {
        super(props);
    }

    _fechar(){
        
        this.props.InformacaoCategoria(false)
    }

   
      
    

    render() {
        console.log('inf é',inf.idh)
        let i = this.props.info
        if(!i){
            return (null)
        }
        else{
            return(
                <ul className='pop'>
                    <li>{inf[i].titulo}</li>
                    <li>{inf[i].unidadeDeMedia}</li>
                    <li>{inf[i].explicacao}</li>
                    <li>{inf[i].dataDado}</li>
                    <li>{inf[i].fonte}</li>
                    <li>{inf[i].url}</li>
                </ul>


            )
        }
                 
        
      
    }
}

export default Informacao;

