import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import "../../assets/Jogar.css";

class NumeroDeCartas extends Component{
    constructor(props){
        super(props)
    }

   render(){
       
    if(this.props.visivel){
           return(null)
       }
       else{
        return(
            <section>
                 <label htmlFor='quantasCartas'>NÃºmero de cartas (por jogador)</label>
                 <br></br>
                 <input type='number' id='testeNumeroDeCartas'></input>
                 <select id='quantasCartas'>
                     <option value='5' name="qtCartas">5</option>
                     <option value='10' name="qtCartas">10</option>
                     <option value='15' name="qtCartas">15</option>
                     <option value='20' name="qtCartas">20</option>
                 </select>
                 <br></br>
                     <label >Escolher cartas</label>
                     <br></br>
                     <input type='radio' name='qlCartas' value='melhores'></input>
                     <input type='radio' name='qlCartas' value='piores'></input>
                     <input type='radio' name='qlCartas' value='aleatorias'></input>
                
                                   
            </section>
 
        )
       }
       
   }
}

export default NumeroDeCartas;
