import React, {Component} from "react"
import './App.css';
import Card from "./components/Card/Card";
import Jogar from "./components/Jogar/Jogar";
import bd from './bancoDeDados/bancoDeDados.json';
import Baralho from "./components/Baralho/Baralho"

class App extends Component{
  constructor(){
    super();

    this.state = {
      cartas:[]
    }
  }

  criarCarta(numero){

    var cartas = []
    for(let i=0;i<numero.length;i++){
      cartas.push({
        pais:bd[i].país,
        idh:bd[i].idh,
        exp:bd[i].exp
      })
    }
    console.log('numero é',numero)
 /*    const dados = bd[1]
    const novaCarta = {pais: bd[1].país,idh:bd[1].idh} */
    const novoEstado = {
      cartas:cartas
    }
    this.setState(novoEstado)
  }

  render(){
    return(
      <section className="conteudo">
        <div id='mandante'>
          <h2>Mandante</h2>
        </div>
        <div id='visitante'>
        <h2>Visitante</h2>
        </div>
        <Baralho cartas={this.state.cartas}/>
        <Jogar class="classeJogar" criarCarta={this.criarCarta.bind(this)}/>
      </section>
    )
  }


}



export default App;
