import React, { Component } from "react"
import './App.css';
import Card from "./components/Card/Card";
import Jogar from "./components/Jogar/Jogar";
import bd from './bancoDeDados/bancoDeDados.json';
import Baralho from "./components/Baralho/Baralho"
import Mandante from "./components/Jogador/Mandante"
import Visitante from "./components/Jogador/Visitante"
import Cabecalho from "./components/cabecalho/Cabecalho";
import Bandeiras from "./components/Bandeiras/Bandeiras"
import { findFlagUrlByCountryName } from "country-flags-svg";
import Placar from "./components/Placar/Placar";
import NumeroDeCartas from "./components/Jogar/NumeroDeCartas";
import Rodape from "./components/Rodape/Rodape"

import Mesa from "./components/Mesa/Mesa"

var fs = require('fs');
var path = require('path');





//ideia -> Pegar os primeiros elementos dos arrays para jogar

class App extends Component {
  constructor() {
    super();

    this.state = {
      mesa: Array.from({ length: bd.length }, (v, k) => k + 1),
      mandante: {
        carta: null,
        visivel: false,
        placar: 0,
        ultimaJogada: null
      },
      visitante: {
        carta: null,
        visivel: false,
        placar: 0
      },
      resultado: {
        texto: null,
        visivel: false,
      },
      jogoEmAndamento: false,
      bandeiras: false


    }
  }




  TodosIds() {

    let todosIds = []
    for (let i = 0; i < bd.length; i++) {
      todosIds.push(bd[i].id)
    }


    return todosIds
  }

  PegarDoBanco(ids) {

    if (Array.isArray(ids)) {
      const dados = []
      ids.map((ids) => {
        for (let i = 0; i < bd.length; i++) {
          if (ids == bd[i].id) {
            dados.push({
              id: bd[i].id,
              pais: bd[i].pais,
              idh: bd[i].idh,
              exp: bd[i].exp,
              bandeira: bd[i].bandeira

            })
          }
        }
      })

      return dados
    }
    else {
      return (
        {
          id: bd[ids].id,
          pais: bd[ids].pais,
          idh: bd[ids].idh,
          exp: bd[ids].exp,
          bandeira: bd[ids].bandeira
        }
      )
    }


  }

  DistribuirCartas(indices) {


    var novoIndice = []
    indices.map((e, index) => index > 1 ? novoIndice.push(e) : '')



    var estadoAntigo = this.state



    this.setState({
      mesa: novoIndice,
      mandante: {
        carta: this.PegarDoBanco(indices[0]),
        visivel: true,
        placar: estadoAntigo.mandante.placar,
        ultimaJogada: estadoAntigo.mandante.ultimaJogada
      },
      visitante: {
        carta: this.PegarDoBanco(indices[1]),
        visivel: false,
        placar: estadoAntigo.visitante.placar
      },
      jogoEmAndamento: true,
      bandeiras: this.BuscarBandeiras(indices)

    })



  }



  BuscarBandeiras(array) {
    var arrayBandeiras = []
    for (let i = 0; i < array.length; i++) {
      arrayBandeiras.push({
        id: array[i],
        nome: bd[array[i]].pais,
        bandeira: bd[array[i]].bandeira,
        visivel: true
      })
    }

    arrayBandeiras.sort(function (a, b) {

      return (a.nome > b.nome) ? 1 : ((b.nome > a.nome) ? -1 : 0);

    });
    return arrayBandeiras
  }



  JogadaMandante(jogada) {

    let prevState = this.state

    let buscarValor = this.state.mandante.carta[jogada]

    let vencedor = this.CompararJogada(jogada, this.state.mandante.carta[jogada], this.state.visitante.carta[jogada])
    let placar = this.AlterarPlacar(vencedor, this.state.mandante.placar, this.state.visitante.placar)



    this.setState({
      mandante: {
        carta: prevState.mandante.carta,
        visivel: true,
        placar: placar.mandante,
        ultimaJogada: jogada
      },
      visitante: {
        carta: prevState.visitante.carta,
        visivel: true,
        placar: placar.visitante
      },
      resultado: {
        texto: placar.mensagemVencedor,
        visivel: true
      }

    })

    setTimeout(() => {
      this.escurecerBandeiras(this.state.mandante.carta.id, this.state.visitante.carta.id)
       this.ProximaJogada(this.state.mesa)

    }, 3000);

  }

  escurecerBandeiras(b1, b2) {

    let band = this.state.bandeiras

    band.map((e) => {
      if (e.id == b1 | e.id == b2) {
        e.visivel = false
      }
    })

  }

  ProximaJogada(cartas) {

    if (cartas.length == 0) {
      this.FimDeJogo()
    }
    else {
      const prevState = this.state

      let novaMesa = [];
      cartas.map((e, index) => index > 1 ? novaMesa.push(e) : '')




      this.setState({
        mesa: novaMesa,
        mandante: {
          carta: this.PegarDoBanco(cartas[0]),
          visivel: true,
          placar: prevState.mandante.placar,
          ultimaJogada: null

        },
        visitante: {
          carta: this.PegarDoBanco(cartas[1]),
          visivel: false,
          placar: prevState.visitante.placar
        },
        resultado: {
          texto: null,
          visivel: false,
        }
      })
    }

  }

  FimDeJogo() {

    let m = this.state.mandante.placar
    let v = this.state.visitante.placar
    let vencedorFinal = ''
    if (m == v) {
      vencedorFinal = ('Fim de jogo: Empate!')
    }
    else if (m>v) {
      vencedorFinal = ('Fim de jogo: Mandante Venceu!')
    }
    else {
      vencedorFinal = ('Fim de jogo: Visitante Venceu!')
    }

    this.setState({
      resultado: {
        texto: vencedorFinal,
        visivel: true
      }
    })
  }

  RecomeçarJogo() {

    var primeiroEstado = {
      mesa: Array.from({ length: bd.length }, (v, k) => k + 1),
      mandante: {
        carta: null,
        visivel: false,
        placar: 0,
        ultimaJogada: null
      },
      visitante: {
        carta: null,
        visivel: false,
        placar: 0
      },
      resultado: {
        texto: null,
        visivel: false,
      },
      jogoEmAndamento: false,
      bandeiras: false
    }
    this.setState(primeiroEstado)
  }

  AlterarPlacar(vencedor, mandanteAtual, visitanteAtual) {


    let placar = {
      mandante: mandanteAtual,
      visitante: visitanteAtual,
      mensagemVencedor: 'Empatou!'
    }

    if (vencedor == 'mandante') {
      placar.mandante += 1
      placar.mensagemVencedor = 'Ganhou!'
      return placar


    }
    else if (vencedor == 'visitante') {
      placar.visitante += 1
      placar.mensagemVencedor = 'Perdeu!'
      return placar

    } else {
      return placar
    }

  }

  CompararJogada(tipo, valorMandante, valorVisitante) {
    if (tipo == 'idh' | tipo == 'exp') {
      if (valorMandante == valorVisitante) {
        return 'empate'
      }
      else if (valorMandante > valorVisitante) {
        return 'mandante'
      }
      else {
        return 'visitante'
      }
    }
  }


  render() {
    return (
      <section className="conteudo" >

        <div id='cabecalho'>
          <Cabecalho vencedorFinal={this.state.vencedorFinal} />
        </div>
        <div id="principal">
          <div id='bandeiras'>
            <Bandeiras bandeiras={this.state.bandeiras} />
          </div>

          <div id='mandante' >

            <Mandante carta={this.state.mandante.carta} JogadaMandante={this.JogadaMandante.bind(this)} visivel={this.state.mandante.visivel} resultado={this.state.resultado.texto} ultimaJogada={this.state.mandante.ultimaJogada} placar={this.state.mandante.placar} />
          </div>
          <div id='visitante'>

            <Visitante carta={this.state.visitante.carta} visivel={this.state.visitante.visivel} resultado={this.state.resultado.texto} placar={this.state.visitante.placar} />
          </div>
          <div id='jogar'>

            <Jogar DistribuirCartas={this.DistribuirCartas.bind(this)} resultado={this.state.resultado} jogoEmAndamento={this.state.jogoEmAndamento} RecomeçarJogo={this.RecomeçarJogo.bind(this)} />
            <Mesa cartas={this.state.mesa} jogoEmAndamento={this.state.jogoEmAndamento} />
          </div>
        </div>
        <div id='rodape'>
          <Rodape />
        </div>

      </section>
    )
  }


}

