import React, {Component} from "react"
import './App.css';
import Card from "./components/Card/Card";
import Jogar from "./components/Jogar/Jogar";
import bd from './bancoDeDados/bancoDeDados.json';
import Baralho from "./components/Baralho/Baralho"
import Mandante from "./components/Jogador/Mandante"
import Visitante from "./components/Jogador/Visitante"

class App extends Component{
  constructor(){
    super();

    this.state = {
      cartas:[],
      mandante:[],
      visitante:[]

    }
  }

   CriarBaralho(){
    console.log('entrei no criar baralho')

    const cartas = []
    for(let i=0;i<bd.length;i++){

      cartas.push({
        id:bd[i].id,
        pais:bd[i].país,
        idh:bd[i].idh,
        exp:bd[i].exp
      })
    }
    console.log('array de cartas é',cartas)
    const novoEstado = {
      cartas:cartas
    }
    this.setState(novoEstado)
  } 

  DistribuirCartas(indices){

    setTimeout(()=>{
      this.setState({
        mandante:[indices[0],indices[1]],
        visitante:[indices[2],indices[3]]
      })
      console.log('new state is',this.state)
    },2000)

  }


  render(){
    return(
      <section className="conteudo" onLoadedData={this.CriarBaralho.bind(this)} >

        <div id='mandante'>
          <h2>Mandante</h2>
        </div>
        <div id='visitante'>
        <h2>Visitante</h2>
        </div>
        <Baralho cartas={this.state.cartas} />
        <Jogar class="classeJogar"  DistribuirCartas={this.DistribuirCartas.bind(this)} CriarBaralho={this.CriarBaralho.bind(this)}/>
        <Mandante className='mandante' mao={this.state.mandante}/>
        <Visitante className='visitante' mao={this.state.visitante}/>

      </section>
    )
  }


}

