import React, { Component } from "react";
import "./estilos.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import bd from '../../bancoDeDados/bancoDeDados.json';

class Jogar extends Component {
    constructor(props){
        super(props);
       
    }
    
    handleIniciarJogo(){
       const nomeDoBotao = 'iniciar jogo'
        let totalDeCartas = bd.length;
        let n = []
        for(let i =0;i<totalDeCartas;i++){
            n.push(i)
        }
       
        n.sort(function(a,b){ return (Math.round(Math.random())-0.5); });
       
            
       
       
        this.props.DistribuirCartas(n)
                
    }

   

    render() {
      
        return (
            <section >
                <button 
                onClick={this.handleIniciarJogo.bind(this)}
                className="btn btn-primary botao_iniciar">{this.nomeDoBotao}</button>
                <div>
                    <h2 className={this.props.resultado.visivel ? 'block' : 'none'}>{this.props.resultado.texto}</h2>
                </div>
               
            </section>
        )
    }
}

export default Jogar;
