import React, { Component } from "react";
import Carousel from 'react-bootstrap/Carousel'
import 'bootstrap/dist/css/bootstrap.min.css';
import before from "../../assets/imagens/before.svg"
import next from "../../assets/imagens/next.svg"
import flag from "../../assets/imagens/flag.svg"
import jogada1 from "../../assets/imagens/jogada1.jpg"
import bcj from "./bancoComoJogar"

//carrossel com react https://reactjsexample.com/a-flexible-and-responsive-carousel-component-for-react/

//https://react-bootstrap.github.io/components/carousel/

class ComoJogar extends Component {
    constructor(props) {
        super(props)
        this.state={
            indice:0
        }
    }
    _proximo(){
        let atual = this.state.indice;
        this.setState({
            indice:atual+1
        })
    }
    render() {

        return (
            <section className='cj'>
                <p className='titulo'>{bcj[this.state.indice].titulo}</p>
                <ul className='cjLista'>
                    <li className='liNavegar'><img src={before} className='imgNavegar' name='before'/></li>
                    <li className='liImg'> <img className='cjImg' src={bcj[this.state.indice].imagem} /></li>
                    <li className='liNavegar'><img src={next} className='imgNavegar' name='next' onClick={(x)=> this._proximo()}/></li>
                </ul>

                <p className='cjExplicacao'>{bcj[this.state.indice].explicacao}</p>
                <p className='indiceExplicacao'>{this.state.indice+1} de {bcj.length}</p>

            </section>


        )
    }
}

export default ComoJogar;

