import React, { Component } from "react";
import "./estilos.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import bd from '../../bancoDeDados/bancoDeDados.json';
import NumeroDeCartas from "./NumeroDeCartas"

class Jogar extends Component {
    constructor(props){
        super(props);
       
    }
    
    handleIniciarJogo(x){
       var num = document.querySelectorAll(`[name='qtCartas']`)
       let n = [...num]
       let quantidadeDeCartas = 0 
       n.map((e)=>e.selected ? quantidadeDeCartas = e.value :'' )
       
       let qCartas = document.querySelectorAll(`[name='qlCartas']`)
       let q = [...qCartas]
       let selecionarCartas = 'aleatoriamente'
        q.map((e)=>e.selected ? selecionarCartas = e.value :'' )


        console.log('numero de cartas: ',quantidadeDeCartas, 'escolher: ',selecionarCartas)
       
        var botaoAtual = x.target.getAttribute('name')

        if(botaoAtual=='Recomeçar Jogo'){
          console.log('recomeçar jogo')
          console.log('props no jogar',this.props)
          this.props.RecomeçarJogo()
        }
        else{
            
        let totalDeCartas = bd.length;
        let n = []
        for(let i =0;i<totalDeCartas;i++){
            n.push(i)
        }
       
        n.sort(function(a,b){ return (Math.round(Math.random())-0.5); });
       
            
       
       
        this.props.DistribuirCartas(n)
        }
                
    }

   

    render() {
      
        return (
            <section >
                <NumeroDeCartas/>
                <button 
                onClick={this.handleIniciarJogo.bind(this)}
                className="btn btn-primary botao_iniciar"
                name={this.props.jogoEmAndamento ? 'Recomeçar Jogo' : 'Iniciar Jogo'}>{this.props.jogoEmAndamento ? 'Recomeçar Jogo' : 'Iniciar Jogo'}</button>
                <div>
                    <h2 className={this.props.resultado.visivel ? 'block' : 'none'}>{this.props.resultado.texto}</h2>
                </div>
               
            </section>
        )
    }
}

export default Jogar;
