import React, { Component } from "react";
import "./estilos.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import bd from '../../bancoDeDados/bancoDeDados.json';



class Card extends Component {
   static n = 0;
    constructor(props){
       super(props);
        
     
   }
   
   
   
   contador(){
    Card.n += 1;
    this.x = Card.n
    return this.x
    }



  

    render() {
        var r = this.props.resultado
        if(r==null){
            console.log('o resultado é null no card')
            var classeBotao = 'btn btn-secondary btn-block'
        }
        else if(r=='Ganhou!'){
            var classeBotao = 'btn btn-success btn-block'
        }
        else if(r=='Empatou!'){
            var classeBotao = 'btn btn-warning btn-block'
        }
        else{
            var classeBotao = 'btn btn-danger btn-block'
        }
        return (
           <section className='card'  name='xy' id={this.props.id}>
           <img className="card-img-top" src={this.props.bandeira} alt={this.props.país}></img>
            <h5>{this.props.país}</h5>
            <div className='card-body'>
            
                <ul className="list-group list-group-flush" >
                   
                   <label>IDH</label>
                    <li className="list-group-item">
                    <button name="idh" className={classeBotao} >{this.props.idh}</button>
                    </li>
                    <label>Expectativa de vida</label>
                    <li className="list-group-item">
                    <button name='exp' className="btn btn-secondary btn-block">{this.props.exp}</button>
                    </li>
                </ul>
                </div>
            </section>
        )
    }
}

export default Card

