import React, { Component } from "react";
import "./estilos.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import bd from '../../bancoDeDados/bancoDeDados.json';



class Card extends Component {
   constructor(props){
       super(props);
       this.state = {
           idTeste:0
       }
     
   }

    contador(n){
        const novoN = {
            idTeste:n+1
        }
        this.setState(novoN)
   }

    render() {
        return (
           <section className='card' key={this.props.id}>
            <div className='card-header' id={this.contador(this.state.idTeste)}>
            {this.props.pais}
            </div>
                <ul className="list-group list-group-flush" >
                   
                   <label>IDH</label>
                    <li className="list-group-item">{this.props.idh}</li>
                    <label>Expectativa de vida</label>
                    <li className="list-group-item">{this.props.exp}</li>
                </ul>
            </section>
        )
    }
}

export default Card
