import React, { Component } from "react";
import "./estilos.css";
import 'bootstrap/dist/css/bootstrap.min.css';

class NumeroDeCartas extends Component{
    constructor(props){
        super(props)
    }

   render(){
       if(!this.props.visivel){
           return(null)
       }
       else{
        return(
            <section>
                 <label htmlFor='quantasCartas'>NÃºmero de cartas (por jogador)</label>
                 <br></br>
                 <select id='quantasCartas'>
                     <option value='5' name="qtCartas">5</option>
                     <option value='10' name="qtCartas">10</option>
                     <option value='15' name="qtCartas">15</option>
                     <option value='20' name="qtCartas">20</option>
                 </select>
                 <br></br>
                     <label htmlFor='quaisCartas'>Escolher cartas</label>
                     <br></br>
                    <select id='quaisCartas'>
                         <option value="aleatoria" name="qlCartas">Aleatoriamente</option>
                        <option value='melhores' name="qlCartas">Melhores cartas</option>
                        <option value='piores' name="qlCartas">Piores cartas</option>
                    </select>
                                   
            </section>
 
        )
       }
       
   }
}

export default NumeroDeCartas;
