import React, { Component } from "react";
import "./estilos.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import bd from '../../bancoDeDados/bancoDeDados.json';



class Card extends Component {
   static n = 0;
    constructor(props){
       super(props);
        
     
   }
   

   
   contador(){
    Card.n += 1;
    this.x = Card.n
    return this.x
    }

    _Jogou(evento){
        console.log('foi clicado no idh')
        console.log('jogou target',evento)
        let res = {
            campo:evento.name,
            valor:evento.textContent
        }
        console.log('res é',res)
    }

    render() {
        console.log('props no card',this.props)
        return (
           <section className='card' name='xy' id={this.props.id}>
           <img className="card-img-top" src={this.props.bandeira} alt={this.props.país}></img>
            <div className='card-body'>
            <h5>{this.props.país}</h5>
            </div>
                <ul className="list-group list-group-flush" >
                   
                   <label>IDH</label>
                    <li className="list-group-item">
                    <button name="btnIDH" className="btn btn-primary btn-lg btn-block" onClick={(x)=> this._Jogou(x.target)}>{this.props.idh}</button>
                    </li>
                    <label>Expectativa de vida</label>
                    <li className="list-group-item">
                    <button name='btnExp' className="btn btn-primary btn-lg btn-block">{this.props.exp}</button>
                    </li>
                </ul>
            </section>
        )
    }
}

export default Card
