import React, { Component } from "react";
import Card from "../Card/Card";
import Baralho from "../Baralho/Baralho";

class Mandante extends Component {
    constructor(props) {
        super(props)
    }



    _JogadaMandante(elemento) {

        var nome = elemento.getAttribute('name')
        if (nome == 'idh') {

            this.props.JogadaMandante('idh')
        }
        else if (nome == 'exp') {

            this.props.JogadaMandante('exp')
        }

    }

    
    render() {
        console.log('props no mandante',this.props)
        if(this.props.carta.length ==0){
            return(
               <h1>Não há cartas no mandante</h1>
            )
        }else{

            return (
                <section className="mandante" key={this.props.id} onClick={(x) => this._JogadaMandante(x.target)} className={this.props.visivel ? 'block' : 'none'} >
    
                    <Card
    
                        bandeira={this.props.carta.bandeira}
                        id={this.props.carta.id}
                        país={this.props.carta.país} idh={this.props.carta.idh} exp={this.props.carta.exp} />
                </section>
            )
        }
    }
     


}

export default Mandante;

